import mongo from 'mongodb'

let client;
let db;
const config = JSON.parse(require('fs').readFileSync('mongo.config.json'));


export async function init() {
    if(!client) {
        client = await mongo.MongoClient.connect(config.url, {useUnifiedTopology: true})
        db = client.db(config.db)
    }
    return {client, db}
}